import React from 'react'
import './home.css';
import { Component } from 'react';

const API = 'https://api.tvmaze.com/search/shows?q=';

export default class Home extends Component {
  constructor() {
    super();
    this.state = {
      data : [],
      isLoaded: false,
    }
  }

  get_data = (name) => {
    fetch(API + name)
    .then(res => res.json())
    .then(json => {
      this.setState({
        isLoaded: true,
        data: json,
      })
    });
  }

  componentDidMount = (name) => {
    this.get_data("ame")
  }

  render() {
    var {isLoaded, data} = this.state;
    if (!isLoaded) {
      return <div><h1>Loading</h1></div>;
    } else {
      return (
        <div className="info">
          <ul>
            {data.map(item => (
              <li key={item.show.id}>
                <img src={item.show.image.json}></img>
                <h1>{item.show.name}</h1>
              </li>
            ))};
          </ul>
        </div>
      )
    }
  }
}
