import React from 'react'
import './home.css';
import { Component } from 'react';
import { BrowserRouter as Router, NavLink} from 'react-router-dom';
import { CircularProgress } from '@material-ui/core';

const API = 'https://api.tvmaze.com/search/shows?q=';

export default class Home extends Component {
  constructor() {
    super();
    this.state = {
      data : [],
      isLoaded: false,
      value: '',
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  tri_list = (data) => {

  } 

  get_data = (name) => {
    fetch(API + name)
    .then(res => res.json())
    .then(json => {
      this.setState({
        isLoaded: true,
        data: json,
      })
      console.log(json.map(item => item.show.image))
    });
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    this.get_data(this.state.value)
    event.preventDefault();
  }

  render() {
    var {isLoaded, data} = this.state;
    const FilmList = data.map(item => (
        <li key={item.score}>
          <h3>{item.show.name}</h3>
          <NavLink to={"/info/".concat(item.show.name)}><img src={item.show.image.medium} label="test"></img></NavLink>
        </li>
    ))
    return (
      <div className="home">
        <form onSubmit={this.handleSubmit}>
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        <input type="submit" value="Envoyer" />
        </form>
        <ul className="list">
          {FilmList}
        </ul>
      </div>
    )
  }
}
