import React from 'react'
import './home.css';
import { Component } from 'react';

const API = 'https://api.tvmaze.com/search/shows?q=';

export default class Home extends Component {
  constructor() {
    super();
    this.state = {
      data : [],
      isLoaded: false,
      image : "NONE",
      score : 0,
      language: "NONE",
      genre: "NONE",
      status : "NONE",
    }
  }

  say_info = (name) => {
    window.alert(name)
  }

  get_data = (name) => {
    fetch(API + name)
    .then(res => res.json())
    .then(json => {
      this.setState({
        isLoaded: true,
        data: json,
      })
    });
  }

  componentDidMount = (name) => {
    this.get_data("master")
  }

  render() {
    var {isLoaded, data} = this.state;
    if (!isLoaded) {
      return <div><h1>Loading</h1></div>;
    } else {
      return (
        <div className="home">
          <ul className="list">
            {data.map(item => (
              <li key={item.score}>
                <img src={item.show.image.medium} onClick={() => imageClick()}></img>
              </li>
            ))};
          </ul>
        </div>
      )
    }
  }
}
