import React from 'react'
import './info.css';
import { Component } from 'react';

const API = 'https://api.tvmaze.com/search/shows?q=';

export default class Info extends Component {
  constructor() {
    super();
    this.state = {
      data : [],
      isLoaded: false,
      image : "NONE",
      score : 0,
      language: "NONE",
      genre: "NONE",
      status : "NONE",
    }
  }

  get_data = (name) => {
    fetch(API + name)
    .then(res => res.json())
    .then(json => {
      this.setState({
        isLoaded: true,
        data: json,
      })
    });
  }

  parse_data = () => {
    var tmp = data.map;
    this.setState({
      image : this.state.data.image.medium,
      score : this.state.data.score,
      language : this.state.data.show.language,
      status : this.state.data.genre.status,
    })
  }

  componentDidMount = (name) => {
    this.get_data("Mrs.%20America")
    this.parse_data()
  }

  render() {
    var {isLoaded, data, image} = this.state;
    if (!isLoaded) {
      return <div><h1>Loading</h1></div>;
    } else {
      return (
        <div className="info">
          <h1>{image}</h1>
          <ul>
            {data.map(item => (
              <li key={item.show.id}>
                {item.show.name}
              </li>
            ))};
          </ul>
        </div>
      )
    }
  }
}
