import React from 'react'
import './home.css';
import { Component } from 'react';
import { BrowserRouter as Router, NavLink} from 'react-router-dom';
import { CircularProgress } from '@material-ui/core';

const API = 'https://api.tvmaze.com/search/shows?q=';

export default class Home extends Component {
  constructor() {
    super();
    this.state = {
      data : [],
      isLoaded: false,
      value: '',
      img_valid: true,
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  get_data = (name) => {
    fetch(API + name)
    .then(res => res.json())
    .then(json => {
      this.setState({
        isLoaded: true,
        data: json,
      })
    });
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    this.get_data(this.state.value)
    event.preventDefault();
  }

  render() {
    var {isLoaded, data} = this.state;

    return (
      <div className="home">
        <form onSubmit={this.handleSubmit}>
          <input type="text" value={this.state.value} onChange={this.handleChange} />
          <input type="submit" value="Envoyer" />
          <input type="checkbox" onChange={this.hendleimg} value="X"/>
        </form>
        <ul className="list">
          {data.map(item => {
            if (item.show.image) {
              return (
                <li key={item.score}>
                  <h3>{item.show.name}</h3>
                  <NavLink to={"/info/".concat(item.show.name)}><img src={item.show.image.medium} /></NavLink>
                </li>
              )
            } else {
              return (
                <li key={item.score}>
                  <h3>{item.show.name}</h3>
                  <NavLink to={"/info/".concat(item.show.name)}><img src="./NONE.png" /></NavLink>
                </li>
              )
            }
          })}
        </ul>
      </div>
    )
  }
}
