import React from 'react'
import './info.css';
import { Component } from 'react';

const API = 'https://api.tvmaze.com/search/shows?q=';

export default class Info extends Component {
  constructor() {
    super();
    this.state = {
      data : [],
      isLoaded: false,
      image : "NONE",
      score : 0,
      language: "NONE",
      genre: "NONE",
      status : "NONE",
      title : "NONE",
    }
  }

  parse_data = (json) => {
    this.setState({
      image: json[0].show.image.medium,
      score: json[0].show.score,
      title: json[0].show.name,
    })
  }

  get_data = (name) => {
    fetch(API + name)
    .then(res => res.json())
    .then(json => {
      this.setState({
        isLoaded: true,
        data: json,
      })
    this.parse_data(json)
    });
  }

  componentDidMount = (name) => {
    this.get_data("Mrs.%20America")
  }

  render() {
    var {isLoaded, data, image} = this.state;
    if (!isLoaded) {
      return <div><h1>Loading</h1></div>;
    } else {
      return (
        <div className="info">
            <h1 className="Title_film">{this.state.title}</h1>
            <img src={this.state.image}></img>
        </div>
      )
    }
  }
}
