import React from 'react'
import './info.css';
import { Component } from 'react';

const API = 'https://api.tvmaze.com/search/shows?q=';

export default class Info extends Component {
  constructor() {
    super();
    this.state = {
      data : [],
      isLoaded: false,
      image : "NONE",
      score : 0,
      language: "NONE",
      genre: "NONE",
      status : "NONE",
    }
  }

  get_data = (name) => {
    fetch(API + name)
    .then(res => res.json())
    .then(json => {
      this.setState({
        isLoaded: true,
        data: json,
      })
    });
  }

  componentDidMount = (name) => {
    this.get_data("Mrs.%20America")
    // window.alert(this.state.data[0].json)
    // this.setState({
    //   image: data[0],
    // })
  }

  render() {
    var {isLoaded, data} = this.state;
    if (!isLoaded) {
      return <div><h1>Loading</h1></div>;
    } else {
      return (
        <div className="info">
          <ul>
            {data.map(item => (
              <li key={item.show.id}>
                <img src={item.show.image.medium}></img>
              </li>
            ))};
          </ul>
          <h1>[{score}]</h1>
        </div>
      )
    }
  }
}
