import React from 'react'
import './info.css';
import { Component } from 'react';

const API = 'https://api.tvmaze.com/search/shows?q=Mrs.%20America';

export default class Info extends Component {
  constructor() {
    super();
    this.state = {
      data : [],
      isLoaded: false,
    }
  }
  get_info = async(name) => {
    const api_call = await fetch(API);
    const data = await api_call.json();
    console.log(data);
    this.state.data = data;
  }
  componentDidMount = (name) => {
    fetch(API)
    .then(res => res.json())
    .then(json => {
      this.setState({
        isLoaded: true,
        data: json,
      })
    });
  }
  render() {
    var {isLoaded, data} = this.state;
    if (!isLoaded) {
      return <div><h1>Loading</h1></div>;
    } else {
      return (
        <div className="info">
          <ul>
            {data.map(item => (
              <li key={item.show.id}>
                {item.show.name}
              </li>
            ))};
          </ul>
        </div>
      )
    }
  }
}
