import React from 'react'
import './home.css';
import { Component } from 'react';
import { BrowserRouter as Router, NavLink} from 'react-router-dom';
import { TextField } from '@material-ui/core';

const API = 'https://api.tvmaze.com/search/shows?q=';

export default class Home extends Component {
  constructor() {
    super();
    this.state = {
      data : [],
      isLoaded: false,
      image : "NONE",
      score : 0,
      language: "NONE",
      genre: "NONE",
      status : "NONE",
      search: "NONE",
    }
  }

  say_info = (name) => {

  }

  get_data = (name) => {
    fetch(API + name)
    .then(res => res.json())
    .then(json => {
      this.setState({
        isLoaded: true,
        data: json,
      })
    });
  }

  // componentDidMount = (name) => {
  //   this.get_data("master")
  // }

  render() {
    var {isLoaded, data} = this.state;

    if (!isLoaded) {
      return (
        <div className="home">
          <form onSubmit={this.handleSubmit}>
            <label>
              Nom :
              <input type="text" value={this.state.search} onChange={this.handleChange} />        </label>
            <input type="submit" value="Envoyer" />
          </form>
        </div>
      )
    } else {
      return (
        <div className="home">
          <ul className="list">
            {data.map(item => (
              <li key={item.score}>
                <NavLink to={"/info/".concat(item.show.name)}><img src={item.show.image.medium}></img></NavLink>
              </li>
            ))};
          </ul>
        </div>
      )
    }
  }
}
