import React from 'react'
import './info.css';
import { Component } from 'react';

const API = 'https://hn.algolia.com/api/v1/search?query=';

export default class Info extends Component {
  constructor() {
    super();
    this.state = {
      hits: [],
    }
  }
  get_info = (name) => {
    fetch(API + name)
      .then(response => response.json())
      .then(data => this.setState({ data }));
  }
  render() {
    return (
      <div className="info">

      </div>
    )
  }
}
