import React from 'react'
import './info.css';
import { Component } from 'react';
import { Box, Button,Container } from '@material-ui/core';

const API = 'https://api.tvmaze.com/search/shows?q=';
const API_Flags = 'https://www.countryflags.io/'

export default class Info extends Component {
  constructor() {
    super();
    this.state = {
      data : [],
      isLoaded: false,
      image : "NONE",
      score : 0,
      language: "NONE",
      genre: [],
      status : "NONE",
      title : "NONE",
      resume : "NONE",
      data_premiere : "NONE",
      flags : "NONE",
    }
  }

  find_name = () => {
    let to_parse = window.location.pathname;
    const name = to_parse.split('/')

    return (name[2]);
  }

  parse_data = (json) => {
    this.setState({
      image: json[0].show.image ? json[0].show.image.medium : "/NONE.png",
      score: json[0].score,
      title: json[0].show.name,
      language: json[0].show.language,
      genre: json[0].show.genres,
      resume: json[0].show.summary,
      status: json[0].show.status,
      data_premiere: json[0].show.premiered,
    })
  }

  get_data = (name) => {
    fetch(API + name)
    .then(res => res.json())
    .then(json => {
      this.setState({
        isLoaded: true,
        data: json,
      })
    this.parse_data(json)
    this.set_flagscountry()
    });
  }

  set_flagscountry = () => {
    var { language } = this.state;
    var language_exist = ["Spanish", "ES", "United States", "US"]
  }

  componentDidMount = () => {
    this.get_data(this.find_name())
  }

  render() {
    var {isLoaded, data, image, status, data_premiere} = this.state;
    if (!isLoaded) {
      return <div><h1>Loading</h1></div>;
    } else {
      return (
        <div> 
          <img className="img_film" src={this.state.image}></img>
          <div className="info">
              <h1 className="Title_film">{this.state.title}</h1>
              <h1 className="Score">{this.state.score}</h1>
              <div className="left_box">
                  <h1>{this.state.language} : <img src={"./NONE.png"} /></h1>
                  <h1>Genres : </h1>
                  {data[0].show.genres.map(item => <li>{item}</li>)}
                  <h1>Status : {status}</h1>
                  <h1>Premiere : {data_premiere}</h1>
              </div>
          </div>
              {this.state.resume}
        </div>
      )
    }
  }
}
